import { mount } from "cypress/react18";
import App from "../../src/App";
import "./../../src/dist/output.css";

Cypress.Commands.add("mount", mount);

describe("App.cy.js", () => {
    function itDark(elementToBeDark, elementClassName) {
        cy.get(document.documentElement).then((elem) => {
            elem.should("have.class", "dark");
            elementToBeDark.should("have.class", elementClassName);
        });
    }

    function itLight(elementToBeLight, elementToBeLightClassName) {
        cy.get(document.documentElement).then((elem) => {
            elem.should("have.class", "dark");
            elementToBeLight.should("have.class", elementToBeLightClassName);
        });
    }

    it("desctop playground light", () => {
        cy.mount(<App />);
        cy.viewport(1200, 620);

        itLight(cy.get("[id='fullAp']"), "md:bg-[#eaeaea]");
    });

    it("desctop playground Dark", () => {
        cy.mount(<App />);
        cy.viewport(1200, 620);
        cy.get("[id='dark']").click().wait(1000);

        itDark(cy.get("[id='fullAp']"), "md:dark:bg-[#212121]");
    });

