        cy.viewport(1200, 620);
        cy.get("[id='dark']").click().wait(1000);

        darkOrLight(
            cy.get("[id='fullApp']"),
            "md:dark:text-white",
            cy.get("[id='fullApp']"),
            "text-zinc-800"
        );
    });
});
