import React, { useState } from "react";
import Definition from "./Definition";
import DarkLightToggle from "./DarkLightToggle";
import ConectMe from "./ConectMe";

import profileLight from "./images/profileLight.jpg";
import signature_White from "./images/signature_White.png";
import githubLight from "./images/githubLight.png";
import linkedinLight from "./images/linkedinLight.png";

function App() {
    const [profileSrc, setProfileSrc] = useState(profileLight);
    const [sigSrc, setSigSrc] = useState(signature_White);
    const [gitSrc, setGitSrc] = useState(githubLight);
    const [linkedinSrc, setlinkedinSrc] = useState(linkedinLight);

    return (
        <div className="static flex flex-row-reverse text-zinc-800 dark:text-white">
            <div className="bg-[#eaeaea] w-[70%] flex flex-row-reverse dark:bg-[#212121] h-[720px] ">
                <DarkLightToggle
                    setProfileSrc={setProfileSrc}
                    setSigSrc={setSigSrc}
                    setGitSrc={setGitSrc}
                    setlinkedinSrc={setlinkedinSrc}
                ></DarkLightToggle>
            </div>
            <div className="right bg-[#ff4f93] w-[30%] h-[720px]">
                {
                    //<div className="bg-sigLight dark:bg-sigDark w-16 h-20 bg-no-repeat m-10"></div>
                }
                <img
                    src={sigSrc}
                    alt="signitor"
                    className=" w-24 h-20 m-10"
                ></img>
            </div>
            <Definition profileSrc={profileSrc} />
            <ConectMe gitSrc={gitSrc} linkedinSrc={linkedinSrc} />
        </div>
    );
}

export default App;
