import { mount } from "cypress/react18";
import App from "../../src/App";
import "./../../src/dist/output.css";

Cypress.Commands.add("mount", mount);

describe("App.cy.js", () => {
    function darkOrLight(
        elementToBeDark,
        elementClassName,
        elementToBeLight,
        elementToBeLightClassName
    ) {
        cy.get(document.documentElement).then((elem) => {
            if (elem.hasClass("dark")) {
                elementToBeDark.should("have.class", elementClassName);
            } else {
                elementToBeLight.should(
                    "have.class",
                    elementToBeLightClassName
                );
            }
        });
    }

    it("desctop playground light", () => {
        cy.mount(<App />);
        cy.viewport(1200, 620);

        darkOrLight(
            cy.get("[id='fullAp']"),
            "md:dark:bg-[#212121]",
            cy.get("[id='fullAp']"),
            "md:bg-[#eaeaea]"
        );
    });

    it("desctop playground Dark", () => {
        cy.mount(<App />);
        cy.viewport(1200, 620);
        cy.get("[id='dark']").click().wait(1000);

        darkOrLight(
            cy.get("[id='fullAp']"),
            "md:dark:bg-[#212121]",
            cy.get("[id='fullAp']"),
            "md:bg-[#eaeaea]"
        );
    });

    it("profile image light"),
        () => {
            cy.mount(<App />);
            cy.viewport(1200, 620);

            cy.get(document.documentElement).then((elem) => {
                if (elem.hasClass("dark")) {
                    cy.get("[id='profileID']").should(
                        "have.class",
                        elementClassName
                    );
                } else {
                    elementToBeLight.should(
                        "have.class",
                        elementToBeLightClassName
                    );
                }
            });
        };
});
