import React, { useState } from "react";
import profileDark from "./images/profileDark.jpg";
import profileLight from "./images/profileLight.jpg";

function Definition() {
    const [profileSrc, setProfileSrc] = useState(profileLight);

    var big = document.documentElement;

    if (big.className === "dark") {
        big.classList.remove("dark");
        setProfileSrc(profileLight);
    } else {
        big.classList.add("dark");
        setProfileSrc(profileDark);
    }

    return (
        <div className="flex flex-row-reverse absolute top-1/2 left-[39%] transform -translate-x-[39%] -translate-y-1/2">
            <div className="flex flex-col max-w-sm justify-center ml-20">
                <h2 className="text-lg mb-1 font-medium">
                    Front End Developer
                </h2>
                <h1 className="text-4xl font-bold">Balqees Khanfar</h1>
                <p className="text-sm mt-6 font-medium">
                    There wasn't a bird in the sky, but that was not what caught
                    her attention. It was the clouds. The deep green that isn't
                    the color of clouds, but came with these. She knew what was
                    coming and she hoped she was prepared.
                </p>
                <div className="flex mt-8">
                    <button className="w-[50%] mr-3 ml-2 bg-transparent hover:bg-[#ff4f93] text-[#ff4f93] font-semibold hover:text-white py-2 px-4 border-[3px] border-[#ff4f93] hover:border-transparent rounded-full">
                        Download CV
                    </button>
                    <button className="w-[50%] mr-2 ml-3 bg-transparent hover:bg-[#ff4f93] text-[#ff4f93] font-semibold hover:text-white py-2 px-4 border-[3px] border-[#ff4f93] hover:border-transparent rounded-full">
                        Contact
                    </button>
                </div>
            </div>
            <div>
                <img
                    src={profileSrc}
                    alt="profile image"
                    className="w-[400px] h-[400px] rounded-full"
                ></img>
            </div>
        </div>
    );
}

export default Definition;
